


window.onload = function(){ 
	pantalla=document.getElementById("display");
	addListenerToKey();
}
input="0";
punto=false;
iniciar=true;
term1=0;
term2=0;
opera="";
sign=true; // sign true = positivo // sign false = negativo





function addListenerToKey() {
	var elements = document.getElementsByClassName('tecla');
	for(i=0; i<elements.length; i++) {
		console.log("Se agrega listener a : "+elements[i].getAttribute("id"));
		elements[i].addEventListener("click", keyPress);
	}

}


function showDisplay() {
	toShow = input.toString();
	if ( toShow.length > 8 ) {
		toShow = toShow.substr(0,8);
	}
	pantalla.innerHTML = toShow;
}

function keyPress(ev) {
	key = ev.target.id;
	if ( ( ( key >= 0) && (key <=9 ) ) || ( key == "punto" ) ) {
		numero(key);
	} else if ( key == "on" ) {
		inicializa();
	} else if ( key == "sign" ) {
		signo();
	} else if ( key == "igual" ) {
		resolver();
	} else {
		operacion(key);
	}

	showDisplay();
}

function numero(valor) { //recoge el número pulsado en el argumento.
	if ( input.toString().length == 8 ) {
		playSound();
		return;
	}
	if ( (input=="0") || (iniciar==true) ) { // inicializar un número, 
		input=valor; //guardar número
		if ( valor == "punto" ) { //si escribimos una punto al principio del número
			input="0."; //escribimos 0.
			punto=true; //cambiar estado de la punto
		}
	} else { //continuar escribiendo un número
		if ( (valor == "punto") && (punto == false) ) { //si escribimos una punto decimal pòr primera vez
			input+=".";
			punto=true; //cambiar el estado de la punto  
		} else if ( (valor=="punto") && (punto==true) ) {
			// Nada
		} else {
			input+=valor
		}
	}
	iniciar=false;
}

function signo() {
	console.log("Signo: "+sign+" valor : "+input);
	if ( parseInt(input) != 0 ) {
		sign = !sign;
		if ( sign ) {
			if ( input.toString().substr(0,1) == "-" ) {
				input = input.toString().substr(1, input.length);
			}
		} else {
			input = "-"+input;
		}
	}
	console.log("Nuevo signo "+sign+"  Valor: "+input);
}


function operacion(op) {
	if ( op == "raiz" ) {
		opera = op;
		resolver();
	} else {
		if ( opera == "" ) {
			opera = op;
			term1 = input;
			input = "";
			sign = false;
			iniciar = true;
		}
	}
}

function resolver() {
	console.log("Resolver : " +term1 + " " + opera+ " " + input);
	switch (opera) {
		case "mas":
			input = parseFloat(term1) + parseFloat(input);
			break;
		case "menos":
			input = parseFloat(term1) - parseFloat(input);
			break;
		case "por":
			input = parseFloat(term1) * parseFloat(input);
			break;
		case "dividido":
			input = parseFloat(term1) / parseFloat(input);
			break;
		case "raiz":
			input = Math.sqrt(parseFloat(input));			
			break;
	}
	term1 = input;
	limpia();
}

function limpia() {
	opera = "";
	sign = true;
	iniciar = true;
	if ( input.toString().substr(0,1) == "-" ) {
		sign = false;
	} 
}
function inicializa() {
	input = "0";
	iniciar=true;
	term1=0;
	term2=0;
	opera="";
	punto=false;
}


var playSound = (function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    return function() {     
        snd.play(); 
    }
})();